use gmr;

 ALTER TABLE `company_details` CHANGE `id` `id` INT(6) NOT NULL AUTO_INCREMENT;

ALTER TABLE company_details ADD COLUMN creation_date DATETIME;
ALTER TABLE company_details ADD COLUMN last_update DATETIME;
ALTER TABLE company_details ADD COLUMN last_updated_by VARCHAR(150);
delimiter //
CREATE TRIGGER company_details_insert_trigger BEFORE INSERT ON company_details
FOR EACH ROW BEGIN
    SET NEW.creation_date=NOW();
    SET NEW.last_updated_by=USER();
    SET NEW.last_update=NOW();
END;//
CREATE TRIGGER company_details_update_trigger BEFORE UPDATE ON company_details
FOR EACH ROW
BEGIN
    SET NEW.last_updated_by=USER();
    SET NEW.last_update = NOW();
END;//
